import React from "react";
import "./App.css";
import mondaySdk from "monday-sdk-js";
import Recorder from "react-mp3-recorder";
import VideoRecorder from "react-video-recorder";

const monday = mondaySdk({
  clientId: "8286fbdc70dd761eb957ef68787b4511",
  apiToken:
    "eyJhbGciOiJIUzI1NiJ9.eyJ0aWQiOjExMzE4NzU1NywidWlkIjoyMjYwNDQ1MCwiaWFkIjoiMjAyMS0wNi0xMFQxMDowNTo1MS44NzdaIiwicGVyIjoibWU6d3JpdGUiLCJhY3RpZCI6OTE5NjAxNCwicmduIjoidXNlMSJ9.MIJ6sN_Rqnfkpfkl51eBCBQMgx_RPNvBGRXMlmTrUKs",
});

class AppSolution extends React.Component {
  constructor(props) {
    super(props);

    // Default state
    this.state = {
      settings: {},
      context: {},
      name: "",
    };
  }

  componentDidMount() {
    // TODO: set up event listeners
    monday.listen("settings", (res) => {
      this.setState({ settings: res.data });
    });

    monday.api(`query { me { name } }`).then((res) => {
      this.setState({ name: res.data.me.name });
    });
  }
  debugger;

  _onRecordingComplete = (blob) => {
    debugger;
    console.log("recording", blob);
  };

  _onRecordingError = (err) => {
    debugger;
    console.log("recording error", err);
  };

  _blobToFile = (theBlob, fileName) => {
    //A Blob() is almost a File() - it's just missing the two properties below which we will add
    theBlob.lastModifiedDate = new Date();
    theBlob.name = fileName;
    return theBlob;
  };
  _handleBlob = (blob) => {
    const file = this._blobToFile(blob, "myFileName");
    monday.get("context").then((res) => {
      debugger;
    });
  };

  render() {
    // const newFile = this._blobToFile("", "myNewFileName");

    return (
      <div
        className="App"
        style={{ background: this.state.settings.background }}
      >
        Hello, {this.state.name}!
        {/* <Recorder
          onRecordingComplete={this._onRecordingComplete}
          onRecordingError={this._onRecordingError}
        /> */}
        <VideoRecorder
          onRecordingComplete={(videoBlob) => {
            // Do something with the video...
            this._handleBlob(videoBlob);
          }}
        />
      </div>
    );
  }
}

export default AppSolution;
