import React, { useEffect, useState } from "react";
import "./App.css";
import "react-voice-recorder/dist/index.css";
import useRecorder from "./components/useRecorder";
import { useCallback } from "react";
import {
  _blobToFile,
  _createUpdate,
  _addFileToUpdate,
  getVoiceMessagesHistory,
  getItemID
} from "./utils/helper";
import { MainScreen } from "./components/MainScreen";

const AppSolution = ({mondayInstance}) => {
  // const [settings, setSettings] = useState(null);
  const [userName, setUserName] = useState(null);

  let [audioURL, isRecording, startRecording, stopRecording, blob] =
    useRecorder();

  const _handleBlob = useCallback(async (blob) => {
    try {
      const file = _blobToFile(blob, "myFileName");
      const mondayContextResponse = await mondayInstance.get("context");
      const { itemId, boardId } = mondayContextResponse.data;
      // const updates = await getUpdates();
      const newUpdateId = await _createUpdate(itemId);
      await _addFileToUpdate(newUpdateId, file);
    } catch (error) {}
  }, [mondayInstance]);

  useEffect(() => {
    // mondayInstance.listen("settings", (res) => {
    //   setSettings(res.data);
    // });

    if (blob) {
      _handleBlob(blob);
    }
  }, [blob, _handleBlob]);

  useEffect(() => {
    const fetchData = async () => {
      const itemId = await getItemID(mondayInstance);
      const messagesHistory = await getVoiceMessagesHistory(itemId);
      debugger;
    };
    fetchData();
  }, []);

  return (
    <div className="App">
      <MainScreen />
      {userName && `Hello, ${userName}`}
      <audio src={audioURL} controls />
      <button onClick={startRecording} disabled={isRecording}>
        start recording
      </button>
      <button onClick={stopRecording} disabled={!isRecording}>
        stop recording
      </button>
    </div>
  );
};

export default AppSolution;
