import React from "react";
import { Colors } from "../ui/Colors";
import { Header1, Header2, Header3, FlexedColCenter } from "../ui/Layouts";
import styled from "styled-components";

const Container = styled(FlexedColCenter)``;
const Title = styled(Header1)`
  color: ${Colors.black};
`;
const Subtitle = styled(Header2)`
  color: ${Colors.black};
`;
const ActionText = styled(Header3)`
  color: ${Colors.grey};
`;

export const MainScreen = ({ startRecord }) => {
  return (
    <Container>
      <button onClick={startRecord}>mic icon</button>
      <ActionText>Start recording now</ActionText>
    </Container>
  );
};
