import axios from "../api/axios";

export const getCurrentItemID = async (mondayInstance) => {
  try {
    const contextResponse = await mondayInstance.get("context");
    const { itemId } = contextResponse.data;
    return itemId;
  } catch (error) {}
};

export const getVoiceMessagesHistory = async (mondayInstance, itemId) => {
  const query = ` query {items( ids : ${itemId}) {
        updates {
          id
          body
          creator_id
          assets {
            id
            public_url
          }
        }
      }}`;

  const updatesResponse = await mondayInstance.api(query);
  const processedUpdates = await getProcessedUpdates(
    mondayInstance,
    updatesResponse
  );
  debugger;
  console.log(processedUpdates);

  //process response...
};

const getProcessedUpdates = async (mondayInstance, updatesResponse) => {
  if (updatesResponse.data.items[0].updates.length > 0) {
    //has updates
    const updatesWithVoiceMemos = updatesResponse.data.items[0].updates.filter(
      ({ assets }) => assets.length && filterAssetsByAssetType(assets)
    );

    // const updatesWithVoiceMemos = updatesWithAssets.filter(({ assets }) =>
    //   filterAssetsByAssetType(assets)
    // );

    // let processedUpdatesWithVoiceMemos = [];
    // const Promises = updatesWithVoiceMemos.map(({ creator_id, assets }) => {
    //   return {
    //     func: getCreatorName,
    //     assets,
    //     creator_id,
    //   };
    // });
    debugger;
    const processedUpdatesWithVoiceMemos = await Promise.all(
      updatesWithVoiceMemos.map(({ id, assets, creator_id }) =>
        getCreatorName(mondayInstance, creator_id).then((creatorName) => {
          const assetId = assets[0].id;
          const assetSrc = assets[0].public_url;
          return { creatorName, updateId: id, assetId, assetSrc };
        })
      )
    );
    // for (const update of updatesWithVoiceMemos) {
    //   const { assets, creator_id } = update;
    //   const creatorName = await getCreatorName(mondayInstance, creator_id);
    //   processedUpdatesWithVoiceMemos.push({ ...assets[0], ...creatorName });
    // }
    // debugger;
    return processedUpdatesWithVoiceMemos;
    // return updatesWithVoiceMemos.map(({ assets, creator_id }) => {
    //   const creatorName = await getCreatorName(mondayInstance, creator_id);
    //   return { ...assets, creatorName };
    // });
  }
  return null;
};

const filterAssetsByAssetType = (assets) => {
  return assets.filter(({ public_url }) => public_url.includes(".webm"));
};

// const getVoiceMemos = (mondayInstance, assets, creator_id) => {
//   const assetsWithVoiceMemos = assets.filter((asset) =>
//     filterAssetByAssetType(asset)
//   );
// };

// const filterAssetByAssetType = (asset) => {
//   return asset.public_url.includes(".webm");
// };

const getCreatorName = async (mondayInstance, creator_id) => {
  const query = ` query { users ( ids : ${creator_id} ) { name }
         }`;

  const response = await mondayInstance.api(query);
  return response.data.users[0].name;
};

export const _blobToFile = (theBlob, fileName) => {
  theBlob.lastModifiedDate = new Date();
  theBlob.name = fileName;
  const processedBlob = theBlob.slice(0, theBlob.size, "video/mp4");
  const newFile = new File([theBlob], fileName, {
    type: processedBlob.type,
  });

  return newFile;
};

export const _createUpdate = async (mondayInstance, itemId) => {
  const query = `mutation {
        create_update (item_id: ${itemId}, body: "This update is for a new video") {
        id
        }
        }`;
  const response = await mondayInstance.api(query);

  const { id } = response.data.create_update;
  return id;
};

export const _addFileToUpdate = async (newUpdateId, file) => {
  const formData = new FormData();
  formData.append("variables[file]", file, "filename.mp4");

  const noVariableQuery = `mutation addFile($file: File!) {add_file_to_update (update_id: ${newUpdateId}, file: $file) {id}}`;
  formData.append("query", noVariableQuery);

  try {
    const res = await axios.post(process.env.REACT_APP_BASE_URL, formData);
  } catch (error) {
    //internal 500 server error
  }
};

export const _createColumn = async (mondayInstance, boardId, columnName) => {
  const query = `mutation {
        create_column (board_id: ${boardId}, title: ${columnName}, column_type: file) {
        id
        }
        }`;
  const response = await mondayInstance.api(query);
  const { id } = response.data.create_column;
  return id;
};

export const _addFileToColumn = async (itemId, newColumnId, file) => {
  const formData = new FormData();
  formData.append("variables[file]", file, "filename.mp4");

  const noVariableQuery = `mutation addFile($file: File!) {  add_file_to_column (item_id: ${itemId}, column_id: ${newColumnId},file: $file) {id}}`;
  formData.append("query", noVariableQuery);

  try {
    const res = await axios.post(process.env.REACT_APP_BASE_URL, formData);
  } catch (error) {
    //internal 500 server error
  }
};
