import React from "react";
import { Colors } from "../ui/Colors";
import { Header1, Header2, Header3 } from "../ui/Layouts";
import styled from "styled-components";

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`;
const Title = styled(Header1)`
  color: ${Colors.black};
`;
const Subtitle = styled(Header2)`
  color: ${Colors.black};
`;
const ActionText = styled(Header3)`
  color: ${Colors.grey};
`;

export const MainScreen = ({ startRecord }) => {
  return (
    <Container>
      <Title>Welcome to Voice Description!</Title>
      <Subtitle>A more effective way to explain your task to others</Subtitle>
      <button onClick={() => startRecord}>mic icon</button>
      <ActionText>Start recording now</ActionText>
    </Container>
  );
};
